import "./App.css";
import { useState } from "react";
import Axios from "axios";

function App() {
  const [user_handle, setUser_Handler] = useState("");
  const [first_name, setFirst_Name] = useState("");
  const [last_name, setLast_Name] = useState("");
  const [phonenumber, setPhonenumber] = useState("");
  const [usersList, setUsers] = useState([]);

  const addUser = () => {
    Axios.post("http://localhost:3001/create", {
      user_handle: user_handle,
      first_name: first_name,
      last_name: last_name,
      phonenumber: phonenumber,
    }).then(() => {
      alert("empleado registrado");
    });
  }

  const getUsers = () => {
    Axios.get("http://localhost:3001/users").then((response) => {
      setUsers(response.data);
    });
  }

  const updateUser = () => {
    Axios.put("http://localhost:3001/users")
  }

  return (
    <div className="App">
      <div className="Datos">
        <label>
          user_handle{" "}
          <input
            onChange={(event) => {
              setUser_Handler(event.target.value);
            }}
            type="text"
          ></input>
        </label>
        <label>
          first_name{" "}
          <input
            onChange={(event) => {
              setFirst_Name(event.target.value);
            }}
            type="text"
          ></input>
        </label>
        <label>
          last_name{" "}
          <input
            onChange={(event) => {
              setLast_Name(event.target.value);
            }}
            type="text"
          ></input>
        </label>
        <label>
          phonenumber{" "}
          <input
            onChange={(event) => {
              setPhonenumber(event.target.value);
            }}
            type="text"
          ></input>
        </label>
        <button onClick={addUser}>Registrar</button>

        <button onClick={getUsers}>Mostrar Usuarios</button>
      </div>
      <div className="List">
        <table>
          <thead>
            <tr>
              <th>Nick Name</th>
              <th>Nombre</th>
              <th>Apellido</th>
              <th>Telefono</th>
            </tr>
          </thead>
          <tbody>
            {
              usersList.map((val,key) =>{
                return (
                  <tr>
                    <th>{val.user_handle}</th>
                    <th>{val.first_name}</th>
                    <th>{val.last_name}</th>
                    <th>{val.phonenumber}</th>
                  </tr>
                )
              })
            }
          </tbody>
        </table>



      </div>
    </div>
  );
}

export default App;
